import React from 'react'
import styled from 'styled-components'
import Logo from '@components/common/Logo'

const FooterContainer = styled.footer`
  min-height: 20vh;
  padding: 20px;
  color: #fff;
  background-color: #d0c4b8;

  ul {
    padding: 0;
  }

  li {
    list-style: none;
  }

  a {
    text-decoration: none;
  }
`

const Wrapper = styled.section`
  display: flex;
  justify-content: space-between;
  align-items: center;
`

const FooterLinks = styled.section`
  display: flex;
  gap: 20px;
`

const ButtonsContainer = styled.span`
  border: 2px solid #fff;
  height: 32px;
  width: 32px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 200px;
`

const FooterButtons = styled.section`
  display: flex;
  flex-direction: column;
  gap: 20px;
`

const Footer = () => {
  const footerButtons = [
    {
      link: '#',
      name: 'tw'
    },
    {
      link: '#',
      name: 'ig'
    },
    {
      link: '#',
      name: 'fb'
    }
  ]

  const footerLinks = [
    {
      links: [
        {
          link: '#',
          name: 'iOS App Store'
        },
        {
          link: '#',
          name: 'Google Play Store'
        }
      ],
      name: 'Download'
    },

    {
      links: [
        {
          link: '#',
          name: 'Privacy Policy'
        },
        {
          link: '#',
          name: 'Terms of Service'
        }
      ],
      name: 'Legal'
    },
    {
      links: [
        {
          link: '#',
          name: 'Contact'
        },
        {
          link: '#',
          name: 'Help/FAQ'
        }
      ],
      name: 'Help'
    }
  ]
  return (
    <FooterContainer>
      <Logo type='footer' />
      <Wrapper>
        <FooterLinks>
          {footerLinks.map(({ links, name }) => (
            <ul key={name}>
              <li>{name}</li>
              {links.map(({ link, name }) => (
                <li key={name}>
                  <a href={link}>{name}</a>
                </li>
              ))}
            </ul>
          ))}
        </FooterLinks>

        <FooterButtons>
          <h4>admin@buuks.uk</h4>
          {footerButtons.map(({ link, name }) => (
            <ButtonsContainer key={name}>
              <h4>{name}</h4>
            </ButtonsContainer>
          ))}
        </FooterButtons>
      </Wrapper>
    </FooterContainer>
  )
}

export default Footer
