import { useState, useEffect } from 'react'

const useDebouncedCallback = <T extends (...args: any[]) => any>(callback: T, delay: number): T => {
  // State to store the current callback timeout ID
  const [timeoutId, setTimeoutId] = useState<NodeJS.Timeout>()

  // The debounced callback function that will be returned
  const debouncedCallback = (...args: Parameters<T>): void => {
    // Clear any existing timeout
    clearTimeout(timeoutId)
    // Set a new timeout to call the callback after the specified delay
    setTimeoutId(
      setTimeout(() => {
        callback(...args)
      }, delay)
    )
  }

  // Clean up the timeout when the component using the hook unmounts
  useEffect(() => {
    return () => {
      clearTimeout(timeoutId)
    }
  }, [timeoutId])

  return debouncedCallback as T
}

export default useDebouncedCallback
