import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

export const getPosts = createAsyncThunk('post/getPosts', async (thunkAPI) => {
  return fetch('https://jsonplaceholder.typicode.com/posts').then((res) => res.json())
})

interface PostState {
  post: unknown[]
  loading: boolean
}

const initialState: PostState = {
  post: [],
  loading: false
}

const postSlice = createSlice({
  name: 'posts',
  initialState,
  extraReducers: {
    [getPosts.pending as any]: (state: { loading: boolean }, action: any) => {
      state.loading = true
    },
    [getPosts.fulfilled as any]: (state: { loading: boolean; posts: any }, action: { payload: Record<string, unknown> }) => {
      state.loading = false
      state.posts = action.payload
    },
    [getPosts.rejected as any]: (state: { loading: boolean }, action: any) => {
      state.loading = false
    }
  }
})

export default postSlice.reducer
