import buuks from '@/config/services/buuks'
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import constant from '@/config/constants'

const {
  API: { routes }
} = constant

// interface FetchBooksArgs {
//   argument: string
// }

// interface FetchBooksResult {
//   // the shape of the data returned by the API
// }

export const fetchBooks = createAsyncThunk<any, any, any>('books/getBooks', async (a, thunkAPI) => {
  const response = await buuks(routes.searchBooks, { title: a })
  return response
})

interface BookState {
  books: Record<string, unknown>
  loading: boolean
}

const initialState: BookState = {
  books: {},
  loading: false
}

const bookSlice = createSlice({
  name: 'books',
  initialState,
  extraReducers(builder) {
    builder
      .addCase(fetchBooks.pending, (state) => {
        state.loading = true
      })
      .addCase(fetchBooks.fulfilled, (state, action) => {
        state.loading = true
        state.books = action.payload
      })
      .addCase(fetchBooks.rejected, (state) => {
        state.loading = false
      })
  },
  reducers: {}
})

export default bookSlice.reducer
