import React from 'react'
import styled from 'styled-components'
import icons from '@components/icons'

const SearchWrapper = styled.div`
  position: relative;
  input::placeholder {
    color: red;
    font-weight: 600;
  }
`

const SearchIcon = styled.div`
  position: absolute;
  top: 18px;
  left: 20px;
`

const Search = styled.input`
  border-radius: 200px;
  background-color: #f5efe5;
  border: none;
  height: 40px;
  width: 300px;
  padding: 10px 20px 10px 40px;
  margin: 8px;

  &:focus-visible {
    outline: 1px solid #8c7b72;
  }

  &::placeholder {
    content: 'Search...';
    color: gray;
  }
`

const SearchInput = () => {
  return (
    <SearchWrapper>
      <SearchIcon>{icons.SearchIcon()}</SearchIcon>
      <Search type='search' placeholder='Search for books' className='rounded-search-input' />
    </SearchWrapper>
  )
}

export default SearchInput
