import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

export const fetchBooks = createAsyncThunk('books/getBooks', async () => {
  return fetch('http://openlibrary.org/search.json?title=drac').then((res) => res.json())
})

interface BookState {
  numFound: number
  start: number
  numFoundExact: boolean
  docs: unknown[]
  num_found: number
  q: string
  offset: null
}

const initialState: BookState = {
  numFound: 0,
  start: 0,
  numFoundExact: true,
  docs: [],
  num_found: 0,
  q: '',
  offset: null
}

const bookSlice = createSlice({
  name: 'books',
  initialState,
  extraReducers: {
    [getBooks.pending as any]: (state: { loading: boolean }, action: any) => {
      state.loading = true
    },
    [getBooks.fulfilled as any]: (state: { loading: boolean; posts: any }, action: { payload: Record<string, unknown> }) => {
      state.loading = false
      state.posts = action.payload
    },
    [getBooks.rejected as any]: (state: { loading: boolean }, action: any) => {
      state.loading = false
    }
  }
})

export default bookSlice.reducer
