import logger from '@/logger.config'
import searchBooks from '@config/services/search'
import { useEffect, useState } from 'react'
// import logger from '@logger'

const useGetBooks = (search: string): any => {
  const [a, b] = useState()
  const [filter, setFilter] = useState<Record<string, string | boolean | number | undefined>>({})

  useEffect(() => {
    setFilter({ title: search })
  }, [search])

  const fetcher = async () => {
    try {
      const response = await searchBooks.getBooks(filter)
      b(response as any)
      return response
    } catch (error) {
      logger(error)
    }
  }

  useEffect(() => {
    fetcher()
  }, [search])

  return a
}

export default useGetBooks
