import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

interface FetchBooksArgs {
  argument: string
}

interface FetchBooksResult {
  // the shape of the data returned by the API
}

export const fetchBooks = createAsyncThunk<FetchBooksResult, FetchBooksArgs, any>('books/getBooks', async (a, thunkAPI) => {
  console.log('tdfgjhkjlk', a.argument)
  return await fetch(`http://openlibrary.org/search.json?title=${a.argument}`).then((res) => res.json())
})

// export const fetchBooks = createAsyncThunk('books/getBooks', async (a) => {
//   console.log('tdfgjhkjlk', a)
//   return await fetch('http://openlibrary.org/search.json?title=drac').then((res) => res.json())
// })

interface BookState {
  books: Record<string, unknown>
  loading: boolean
}

const initialState: BookState = {
  books: {},
  loading: false
}

const bookSlice = createSlice({
  name: 'books',
  initialState,
  extraReducers(builder) {
    builder
      .addCase(fetchBooks.pending, (state) => {
        state.loading = true
      })
      .addCase(fetchBooks.fulfilled, (state, action) => {
        state.loading = true
        state.books = action.payload
      })
      .addCase(fetchBooks.rejected, (state) => {
        state.loading = false
      })
  },
  reducers: {}
})

export default bookSlice.reducer
