import React from 'react'
import styled from 'styled-components'
import Logo from '@components/common/Logo'

const FooterContainer = styled.footer`
  min-height: 20vh;
  padding: 20px;
  color: #fff;
  background-color: #d0c4b8;

  ul {
    padding: 0;
  }

  li {
    list-style: none;
  }
`

const FooterLinks = styled.section`
  display: flex;
  margin-right: calc(2.25rem * var(--tw-space-x-reverse));
  margin-left: calc(2.25rem * calc(1 - var(--tw-space-x-reverse)));
`

const Footer = () => {
  const footerButtons = [
    {
      link: '#',
      name: 'tw'
    },
    {
      link: '#',
      name: 'ig'
    },
    {
      link: '#',
      name: 'fb'
    }
  ]

  const footerLinks = [
    {
      links: [
        {
          link: '#',
          name: 'iOS App Store'
        },
        {
          link: '#',
          name: 'Google Play Store'
        }
      ],
      name: 'Download'
    },

    {
      links: [
        {
          link: '#',
          name: 'Privacy Policy'
        },
        {
          link: '#',
          name: 'Terms of Service'
        }
      ],
      name: 'Legal'
    },
    {
      links: [
        {
          link: '#',
          name: 'Contact'
        },
        {
          link: '#',
          name: 'Help/FAQ'
        }
      ],
      name: 'Help'
    }
  ]
  return (
    <FooterContainer>
      <Logo type='footer' />
      <FooterLinks>
        {footerLinks.map(({ links, name }) => (
          <ul key={name}>
            <li>{name}</li>
            {links.map(({ link, name }) => (
              <li key={name}>
                <a href={link}>{name}</a>
              </li>
            ))}
          </ul>
        ))}
      </FooterLinks>
    </FooterContainer>
  )
}

export default Footer
