import configConstants from '@config/constants'

const {
  API: { baseURL, timeout }
} = configConstants

const buuks = async (route: string, filter?: Record<string, string | boolean | number | undefined>) => {
  const params = Object.entries(filter || {})
    .map((arr) => `${arr[0]}=${arr[1]}`)
    .toString()
    .replaceAll(',', '&')
  return await fetch(`${baseURL}${route}${Object.keys(filter || {}).length > 0 ? '?' : ''}${params}`)
}

export default buuks
